// Function to generate sampleSet header string
function gen_sampleSet_hdr_str()
{
    // Retreive global PUT variable
    var PUT = context.global.PUT;

    var PUT_ctrl_hdr = PUT.control_hdr;
    var lower_bound = "";
    var upper_bound = "";
    var steps = "";
    for (var key in PUT_ctrl_hdr)
    {
    	// Sort values in ascending order
    	var values = PUT_ctrl_hdr[key].values.sort(function(a, b){return a - b;});

    	// Calculate step width
		var step = 0;
		if(values.length == 1)
		{
			step = values[0];
		}
		else if(values.length > 1)
		{
			var array_sum = values.reduce(function (a, b) {return a + b;}, 0);
			step = 2*(array_sum - values.length * values[0]) / ( values.length * (values.length - 1) );
		}

    	lower_bound = lower_bound + values[0] + ",";
    	upper_bound = upper_bound + values[values.length - 1] + ",";
    	steps = steps + step + ",";
    }
    lower_bound = lower_bound.slice(0, -1);
    upper_bound = upper_bound.slice(0, -1);
    steps = steps.slice(0, -1);

    return "[ " + lower_bound + " ; " + upper_bound + " ; " + steps + " ]";
}

// Function to calculate average of an array
function AVG(data)
{
	if(data.length === 0)
		return 0;
	else
	    return (data.reduce(function (a,b) { return a+b; }) / data.length);
}

// Relative [population] standard deviation calculation of an array
function STD_rel(data)
{
	// Calculate average
	var avg = AVG(data);

	if(avg === 0)
		return 0;
	else
    	// Calculate relative standard daviation
		return Math.sqrt(AVG(data.map(function (val) { return Math.pow(val-avg, 2); }))) / avg;
}

// Median calculation of an array
function MEDIAN(data)
{
    // Sort data in ascending order
    var sorted_data = data.slice(0).sort( function(a,b) {return a - b;} );

    // Calculate the half point
    var half = Math.floor(sorted_data.length/2);

    // If data length is odd, return the middle value
    if (sorted_data.length % 2)
        return sorted_data[half];
    // If data length is even, return the average of the middle two values
    else
        return (sorted_data[half-1] + sorted_data[half]) / 2.0;
}

// Median Absolute Deviation calculation
function MAD (data)
{
    var median = MEDIAN(data);
    var absoluteDeviation = data.map(function (val)
    {
        return Math.abs(val - median);
    });

    return MEDIAN(absoluteDeviation);
}

// Calculate data outliers using Iglewicz and Hoaglin method.
// values with a Z-score > 3.5 are considered potential outliers
function indexOfOutliers (data)
{
	var dataMedian = MEDIAN(data);
	var dataMAD = MAD(data);

	return data.reduce(function (res, val, i)
	{
		if (Math.abs((0.6745 * (val - dataMedian)) / dataMAD) > 3.5)
			res.push(i);
		return res;
	}, []);
}

