cooja.motetype              = {};
cooja.motetype.CONTIKI_DIR  = CONTIKI_DIR;
cooja.motetype.csc_path_rel = csc_path_rel;
msg.cooja                   = cooja;

return msg;

if(msg.payload.indexOf("pts_mux has STARTED on port ") !== -1)
{
    context.pts_mux_port = msg.payload.split(" ")[5];
    context.pts_mux_PID  = msg.payload.split(" ")[8];
}
else
{
    context.ptyFiles_IDs = msg.payload;
}

if(typeof context.pts_mux_port !== 'undefined' && typeof context.pts_mux_PID !== 'undefined' && typeof context.ptyFiles_IDs !== 'undefined')
//if(typeof context.ptyFiles_IDs !== 'undefined')
{
    var ptyFiles_IDs = context.ptyFiles_IDs;
    var moteIDs = {};
    var GROUPs = context.global.GROUPs;

    // Make sure the number of assigned and configured pts devices are the same
    var pts_count = ptyFiles_IDs.length;
    var sink_count = GROUPs.sink.length;
    var source_count = GROUPs.source.length;
    var disturber_count = GROUPs.disturber.length;
    if(pts_count != sink_count + source_count + disturber_count)
    {
        msg.error = "Assigned and configured nodes count does not match";
        delete msg.payload;
        return [null, msg];
    }

    // Match [sink,source,disturber] nodes to pts devices
	outer_loop:
    for(var i = 0; i < ptyFiles_IDs.length; i++)
    {
        var moteID  = parseInt(ptyFiles_IDs[i].split('\t')[0]);
        var ptyFile = ptyFiles_IDs[i].split('\t')[1];
        moteIDs[ptyFile] = moteID;

        // Search mote ID within sink nodes
        for(var j = 0; j < sink_count; j++)
        {
            if(GROUPs.sink[j].ID === moteID)
            {
                GROUPs.sink[j].ptsFile = ptyFile;
                continue outer_loop;
            }
        }
        // Search mote ID within source nodes
        for(var k = 0; k < source_count; k++)
        {
            if(GROUPs.source[k].ID === moteID)
            {
                GROUPs.source[k].ptsFile = ptyFile;
                continue outer_loop;
            }
        }
        // Search mote ID within disturber nodes
        for(var l = 0; l < disturber_count; l++)
        {
            if(GROUPs.disturber[l].ID === moteID)
            {
                GROUPs.disturber[l].ptsFile = ptyFile;
                continue outer_loop;
            }
        }

        msg.error = "simulated ptsFile = " + ptyFile + " is not part of configured nodes";
        delete msg.payload;
        return [null, msg];
    }

    context.global.GROUPs       = GROUPs;
    context.global.pts_mux_port = context.pts_mux_port;
    context.global.pts_mux_PID  = context.pts_mux_PID;
    context.global.moteIDs      = moteIDs;

    delete context.pts_mux_port;
    delete context.pts_mux_PID;
    delete context.ptyFiles_IDs;

    return [{topic: "start_experiment"}, {payload: "Cooja is started on RC" + context.global.RC_ID}];
}

var cooja            = msg.payload[0].cooja;
var topic            = msg.payload[0].topic;
var CONTIKI_DIR      = cooja.motetype.CONTIKI_DIR;
var csc_path_rel     = cooja.motetype.csc_path_rel;
var simulation_mode  = cooja.simulation_mode;
var contiki_log      = cooja.contiki_log + context.global.RC_ID;
var contiki_done_str = cooja.contiki_done_str;

// Sink and source nodes array
var GROUPs = context.global.GROUPs;
var node_IDs = [];
for(var i=0; i<GROUPs.sink.length; i++)
    node_IDs.push( GROUPs.sink[i].ID);
for(var i=0; i<GROUPs.source.length; i++)
    node_IDs.push( GROUPs.source[i].ID);

return {CONTIKI_DIR: CONTIKI_DIR, csc_path_rel: csc_path_rel, simulation_mode: simulation_mode, contiki_log: contiki_log, contiki_done_str: contiki_done_str, node_IDs: node_IDs, topic: topic};

var filePath = msg.CONTIKI_DIR + "/" + msg.csc_path_rel;

// Make cooja pid a global variable
if(typeof(msg.cooja_pid) !== "undefined")
{
    context.global.cooja_pid = msg.cooja_pid;
    delete msg.cooja_pid;
}

// Make simTimer port a global variable
if(typeof(msg.simTimer_port) !== "undefined")
{
    context.global.simTimer_port = msg.simTimer_port;
    delete msg.simTimer_port;
}

// Rename serial2pty_port to port
if(typeof(msg.serial2pty_port) !== "undefined")
{
    msg.port = msg.serial2pty_port;
    delete msg.serial2pty_port;
}

delete msg.CONTIKI_DIR;
delete msg.csc_path_rel;
delete msg.simulation_mode;
delete msg.contiki_log;
delete msg.node_IDs;

msg.payload = "";
return msg;

if(typeof(msg.sample_Idx) !== "undefined")
{
    var payload = msg.payload.payload.split("\n").filter(function(e){return e;});
    // First message
    if(msg.sample_Idx === 0)
    {
        var data = payload[1].split("\t");
        context.iter = 1;

        msg.payload = "H:perf_obj#ITER|txPower,cycleTime,ccaRxCountMax,Retx_max|LAT,ENG\nD:perf_obj\t" + context.iter + "\t" + data[0] + "\t" + data[1] + "\t" + data[2] + "\t" + data[3] + "\t" + data[4] + "\t" + data[5];
        return [msg, null];
    }
    else
    {
        var data = payload[0].split("\t");
        context.iter = context.iter + 1;

        msg.payload = "D:perf_obj\t" + context.iter + "\t" + data[0] + "\t" + data[1] + "\t" + data[2] + "\t" + data[3] + "\t" + data[4] + "\t" + data[5];
        return [null, msg];
    }
}
